var customSchema = new Schema({

    },
    {
        discriminatorKey: '_type'
    }
);

customSchema.statics.createSoireeDateSoiree = function(successCallback, errorCallback, options) {
    //var date = new Date(todaysDate.getTime() + (7 * 24 * 60 * 60 * 1000));
    if (!options) options = {};

    var SoireeDateSoiree = this;

    var soireeType = "soiree_date";
    var soiree;
    if(options.date) {
        soiree = new SoireeDateSoiree({
            soireeType: soireeType,
            date: options.date,
        });
        SoireeDateSoiree.setSoireeDefaults(soiree, successCallback, errorCallback, options);
    }
    else {
        SoireeDateSoiree.determineDateForSoireeType(soireeType, function (date) {
            if (!date) return errorCallback(ErrorCodes.NoAvailableDate);

            soiree = new SoireeDateSoiree({
                soireeType: soireeType,
                date: date
            });
            SoireeDateSoiree.setSoireeDefaults(soiree, successCallback, errorCallback, options);
        });
    }
};

